mapper.select:
SELECT a.SEACH_SEQ, a.TYPE, a.AREAA, a.AREAB, a.AREAC, a.SILVERNAME, a.SERVICE, a.GRADE, a.LAUITUDE, a.LONGITUDE, b.JO_SEQ, b.USERID, b.JO_TYPE, b.JO_DETAILTYPE, b.JO_JOB, b.JO_CONTENT, b.JO_DATE, b.JO_GENDER, b.JO_INT FROM SILVERSEARCH a, JOBOFFER b WHERE a.SEACH_SEQ = b.SEACH_SEQ AND LONGITUDE IN
#{item.longitude}
AND LAUITUDE IN
#{item.lauitude}
ORDER BY b.JO_DATE

SELECT COUNT(a.SEACH_SEQ) FROM SILVERSEARCH a, JOBOFFER b WHERE a.SEACH_SEQ = b.SEACH_SEQ AND a.LONGITUDE IN
#{item.longitude}
AND a.LAUITUDE IN
#{item.lauitude}

SELECT a.SEACH_SEQ, a.TYPE, a.AREAA, a.AREAB, a.AREAC, a.SILVERNAME, a.SERVICE, a.GRADE, a.LAUITUDE, a.LONGITUDE, b.JO_SEQ, b.USERID, b.JO_TYPE, b.JO_DETAILTYPE, b.JO_JOB, b.JO_CONTENT, b.JO_DATE, b.JO_GENDER, b.JO_INT FROM SILVERSEARCH a, JOBOFFER b WHERE a.SEACH_SEQ = b.SEACH_SEQ
AND b.JO_GENDER = #{jo_gender}
AND a.AREAA = #{areaa}
AND a.AREAB = #{areab}
AND b.JO_JOB = #{jo_job}
AND b.JO_DETAILTYPE = #{jo_detailtype}
AND b.JO_TYPE = #{jo_type}
ORDER BY b.JO_DATE
