mapper.insert:
INSERT INTO 
  RESUME
  (
  RE_SEQ
  ,USERID
  ,RE_TYPE
  ,RE_DETAILTYPE
  ,RE_JOB
  ,RE_CONTENT
  ,RE_DATE
  ,RE_AREAA
  ,RE_AREBB
  ,RE_QUALIFICATION
  )
  VALUES
  (
  RE_SEQ.NEXTVAL
  ,#{userid}
  ,#{re_type}
 	,#{re_detailType}
  ,#{re_job}
	,#{re_content}
	,SYSDATE
	,#{re_areaa}
 	,#{re_arebb}
 	,#{re_qualification}
  )

mapper.update:
UPDATE RESUME
  SET
      RE_TYPE = #{re_type}
      ,RE_DETAILTYPE = #{re_detailType}
      ,RE_JOB = #{re_job}
      ,RE_CONTENT = #{re_content}
      ,RE_DATE = SYSDATE
      ,RE_AREAA = #{re_areaa}
      ,RE_AREBB = #{re_arebb}
      ,RE_QUALIFICATION = #{re_qualification}
      
  WHERE 
      USERID = #{userid}
