SELECT SEACH_SEQ, TYPE, AREAA, AREAB, AREAC, SILVERNAME, SERVICE, GRADE, LAUITUDE, LONGITUDE FROM SILVERSEARCH WHERE LONGITUDE IN
#{item.longitude}
AND LAUITUDE IN
#{item.lauitude}
ORDER BY GRADE

SELECT COUNT(SEACH_SEQ) FROM SILVERSEARCH WHERE LONGITUDE IN
#{item.longitude}
AND LAUITUDE IN
#{item.lauitude}
